@router.post("/admin/bulk-price-update")
def run_bulk_price_update(
    db: Session = Depends(get_db),
    admin=Depends(get_current_admin),
):
    """
    Runs all 608 price UPDATE statements in a single transaction.
    Safe to run multiple times  only updates products where is_priced=FALSE.
    """
    updated_total = 0
    errors = []

    try:
        for i, sql in enumerate(PRICE_UPDATES):
            try:
                result = db.execute(text(sql))
                updated_total += result.rowcount
            except Exception as e:
                errors.append({"index": i, "error": str(e), "sql": sql[:80]})

        db.commit()
    except Exception as e:
        db.rollback()
        return {
            "success": False,
            "error": str(e),
            "updated": 0,
        }

    return {
        "success": True,
        "statements_run": len(PRICE_UPDATES),
        "rows_updated": updated_total,
        "errors": errors,
        "message": f"Done! {updated_total} products priced. {len(errors)} errors.",
    }